CREATE TABLE `sdb_pub_files` (
  `file_id` int(11) NOT NULL auto_increment,
  `file_name` varchar(50) default NULL,
  `file_ident` varchar(100) NOT NULL,
  `cdate` int(10) unsigned NOT NULL,
  `memo` varchar(250) default NULL,
